graphql.query:
query {
  product(slug: "base-cotton-t-shirt") {
    id
    name
    slug
    images
    isNew
    isFeatured
    isTrending
    isBestSeller
    averageRating
    reviewCount
    description
    variants {
      id
      sku
      price
      stock
      lowStockThreshold
      barcode
      warehouseLocation
      attributes {
        id
        attribute {
          id
          name
          slug
        }
        value {
          id
          value
          slug
        }
      }
    }
    category {
      id
      name
      slug
    }
    reviews {
      id
      rating
      comment
    }
  }
}

query {
  orderAnalytics(params: { timePeriod: "lastMonth", year: 2025 }) {
    totalOrders
    totalSales
    averageOrderValue
    changes {
      orders
      sales
      averageOrderValue
    }
  }
}

query {
  revenueAnalytics(params: { timePeriod: "lastYear", year: 2025 }) {
    totalRevenue
    changes {
      revenue
    }
    monthlyTrends {
      labels
      revenue
      orders
      sales
      users
    }
  }
}

query {
  userAnalytics(params: { timePeriod: "lastYear", year: 2025 }) {
    totalUsers
    totalRevenue
    retentionRate
    lifetimeValue
    repeatPurchaseRate
    engagementScore
    topUsers {
      id
      name
      email
      orderCount
      totalSpent
      engagementScore
    }
    interactionTrends {
      labels
      views
      clicks
      others
    }
    changes {
      users
    }
  }
}

query {
  interactionAnalytics(params: { timePeriod: "last7days", year: 2025 }) {
    totalInteractions
    byType {
      views
      clicks
      others
    }
    mostViewedProducts {
      productId
      productName
      viewCount
    }
  }
}

query {
  productPerformance(params: { timePeriod: "lastMonth", year: 2025 }) {
    id
    name
    quantity
    revenue
  }
}

query {
  abandonedCartAnalytics(params: { timePeriod: "last7days" }) {
    totalAbandonedCarts
    abandonmentRate
    potentialRevenueLost
  }
}

query {
  searchDashboard(params: { searchQuery: "shirt" }) {
    type
    id
    title
    description
  }
}
